rand_no_x=(double)(rand_r(&seed))/(double)((2<<14)-1); 
rand_no_y=(double)(rand_r(&seed))/(double)((2<<14)-1); 
if (((rand_no_x-0.5) * (rand_no_x-0.5) + 
